    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       double conversor;
       double formula;
        if(jRadioButton1.isSelected()){
           conversor=Double.parseDouble(jTextField2.getText());
            formula=Math.round((conversor-32)/1.8);
           jTextField1.setText(String.valueOf(formula));
       }else if(jRadioButton2.isSelected()){
           conversor=Double.parseDouble(jTextField1.getText());
            formula=Math.round(conversor*1.8+32);
           jTextField2.setText(String.valueOf(formula));
       }
    }//GEN-LAST:event_jButton1ActionPerformed

